select.if:
select * from (select rownum as num, temp.* from (select * from board
where boardCategory = #{categoryCode}
order by boardNo desc)
order by Rating desc)
order by countLike desc)
temp) where num between #{firstNo} and #{lastNo}

select * from (select rownum as num,F.* from (select E.* from (select count(commentbody) as cntcomment,B.groupno from board B full outer join commentbox C on B.groupno = C.groupno group by B.groupno) D, board E where D.groupno=E.groupno
and E.boardCategory = #{categoryCode}
order by cntcomment desc) F) where num between #{firstNo} and #{lastNo}

mapper.select:
select count(*) from board inner join commentbox 
on board.boardno=commentbox.groupno 
where boardno=#{boardNo}
